mapper.insert:
INSERT INTO cart_details (
                          cart_id
                         , product_id
                         , quantity
                         , created_at
                         , updated_at
                         )
VALUES (
        #{cartId}
       , #{productId}
       , #{quantity}
       , NOW()
       , NOW()
       )
    ON DUPLICATE KEY UPDATE
                         quantity = quantity + VALUES(quantity),
                         updated_at = NOW();
