mapper.select:
SELECT * FROM users
WHERE email = #{email}
AND is_deleted = false

SELECT * FROM users
WHERE phone_number = #{phone_number}
AND is_deleted = false

SELECT * FROM users
WHERE username = #{username}
  AND is_deleted = false

mapper.insert:
INSERT INTO users(
    email
    , password
    , username
    , phone_number
)
VALUES (
           #{email}
           , #{password}
           , #{username}
           , #{phone_number}
       )

mapper.update:
UPDATE users
SET  email = #{email}
  , username = #{username}
  , phone_number =  #{phone_number}
  , address=  #{address}
  , role =  #{role}
WHERE user_id = #{user_id}

mapper.delete:
UPDATE users
SET is_deleted = 1
WHERE user_id = #{userID}

mapper.update:
UPDATE users
SET password = #{newPassword}
    ,updated_at=CURRENT_TIMESTAMP
WHERE user_id=#{userID}
AND is_deleted=false
